def A(table_schema):A="\n    SELECT      table_name,\n                column_name,\n                column_default,\n                is_nullable,\n                data_type,\n                character_maximum_length\n    FROM        information_schema.columns\n    WHERE       table_schema = '{0}'\n    ORDER BY    table_name,\n                ordinal_position\n    ".format(table_schema);return A